export type ProposalStatus =
	| { tag: 'Open'; values: null }
	| { tag: 'Closed'; values: null }
	| { tag: 'Accepted'; values: null }
	| { tag: 'Rejected'; values: null }

export interface Proposal {
	abstain_votes: u32
	against_votes: u32
	created_at: u64
	created_by: string
	deadline: u64
	description: string
	for_votes: u32
	id: u32
	proposal_type: ProposalType
	quorum: Option<u32>
	status: ProposalStatus
	title: string
}

